(896, 1, '2016-08-17 01:41:59', '2016-08-17 01:41:59', 'function formats(input){\r\n  var num = input;\r\n  if(!isNaN(num)){\r\n    num = num.toString().split(\'\').reverse().join(\'\').replace(/(?=\\d*\\.?)(\\d{3})/g,\'$1.\');\r\n    num = num.split(\'\').reverse().join(\'\').replace(/^[\\.]/,\'\');\r\n    return num;\r\n  }\r\n}\r\n\r\nfunction formato(num){\r\n	var num = num.toString().replace(/\\./g,\'\');\r\n  	return num;\r\n}\r\n\r\nfunction calculate() {   \r\n  var principal_1 = formato(document.loandatas.principal.value);\r\n  var principal = principal_1.toString().replace(/,/g,\'.\');\r\n  \r\n  var interest_1  = formato(document.loandatas.interest.value); \r\n  var interest_2  = interest_1.toString().replace(/,/g,\'.\'); \r\n  var interest  = interest_2 / 100 / 12;\r\n  \r\n  var payments  = document.loandatas.years.value * 12;   \r\n  \r\n  var x = Math.pow(1 + interest, payments);   \r\n  var monthly   = (principal*x*interest)/(x-1);   \r\n  if (!isNaN(monthly) && (monthly != Number.POSITIVE_INFINITY) && (monthly != Number.NEAGTIVE_INFINITY)) { \r\n    document.loandatas.payment.value = formats(Math.round(monthly));  \r\n    document.loandatas.total.value =  formats(Math.round(monthly*payments));\r\n    document.loandatas.totalinterest.value = formats(Math.round((monthly*payments) - principal));   \r\n  } else {     \r\n    document.loandatas.payment.value=\"\";  \r\n    document.loandatas.total.value=\"\";  \r\n    document.loandatas.totalinterest.value=\"\";   \r\n  } \r\n} \r\n\r\nfunction round(x) {\r\n  return Math.round(x *100)/100;\r\n} \r\n\r\nfunction format(input)\r\n{\r\n  var num = input.value.replace(/\\./g,\'\');\r\n  if(!isNaN(num)){\r\n    num = num.toString().split(\'\').reverse().join(\'\').replace(/(?=\\d*\\.?)(\\d{3})/g,\'$1.\');\r\n    num = num.split(\'\').reverse().join(\'\').replace(/^[\\.]/,\'\');\r\n    input.value = num;\r\n  }\r\n  //else{ alert(\'Solo se permiten numeros\');\r\n  	//input.value = input.value.replace(/[^\\d\\.]*/g,\'\');\r\n  //}\r\n}\r\n', 'calculdora-hipotecaria', '', 'publish', 'closed', 'closed', '', '896', '', '', '2016-12-08 18:41:21', '2016-12-08 18:41:21', '', 0, 'http://estudiocolombia.es/?post_type=custom-css-js&#038;p=896', 0, 'custom-css-js', '', 0),
