(1364, 1, '2017-07-14 00:54:50', '2017-07-14 00:54:50', 'function activar(){\r\n	jQuery(\'#select-status_container ul li\').on(\'click\',function(){\r\n      jQuery(\'#select-min-price_input\').val(\'Todos\');\r\n      jQuery(\'#select-max-price_input\').val(\'Todos\');\r\n      var tipoOperacion = new RegExp(jQuery(\'#select-status_input\').val());\r\n      var tipoOperacionAny = new RegExp(\'any\');\r\n      \r\n      jQuery(\"#select-min-price_container ul li\").each(function(){\r\n        	var valor = jQuery(this).attr(\"id\");\r\n        	var buscar = tipoOperacion.test(valor);\r\n        	var any = tipoOperacionAny.test(valor);\r\n        \r\n      		if(buscar){\r\n            	jQuery(this).css(\"display\",\"block\");\r\n            }else{\r\n            	jQuery(this).css(\"display\",\"none\");\r\n            };\r\n        \r\n        	if(any){\r\n            	jQuery(this).css(\"display\",\"block\");\r\n            }\r\n      });\r\n      \r\n      jQuery(\"#select-max-price_container ul li\").each(function(){\r\n        	var valor2 = jQuery(this).attr(\"id\");\r\n        	var buscar2 = tipoOperacion.test(valor2);\r\n        	var any2 = tipoOperacionAny.test(valor2);\r\n        \r\n      		if(buscar2){\r\n            	jQuery(this).css(\"display\",\"block\");\r\n            }else{\r\n            	jQuery(this).css(\"display\",\"none\");\r\n            };\r\n        \r\n        	if(any2){\r\n            	jQuery(this).css(\"display\",\"block\");\r\n            }\r\n      });\r\n    });\r\n}\r\n\r\nsetTimeout(function(){\r\n	activar();\r\n},2000);\r\n\r\n\r\n', 'script para form busqueda', '', 'publish', 'closed', 'closed', '', 'script-para-form-busqueda', '', '', '2017-07-14 00:54:50', '2017-07-14 00:54:50', '', 0, 'http://estudiocolombia.es/?post_type=custom-css-js&#038;p=1364', 0, 'custom-css-js', '', 0),
